import React from "react";
import classes from "./Title.module.css";

const title = (props) => {
  // return <h1 className={classes.Title}>{props.children}</h1>;
  $(function () {
    for (i = 0; i < 4; i++) {
      $(".buzz_wrapper .text span")
        .eq(0)
        .clone()
        .prependTo(".buzz_wrapper .text");
    }
    for (i = 0; i < 10; i++) {
      $(".buzz_wrapper .scanline").eq(0).clone().appendTo(".buzz_wrapper");
    }
  });

  return (
    <div class="buzz_wrapper">
      <div class="text">
        <span>BUZZZZZZZZZ...</span>
      </div>
      <div class="scanline"></div>
    </div>
  );
};

export default title;
